mapper.select:


     and user_id = #{userId}
      and article_title like concat('%', #{articleTitle}, '%')
     and article_type = #{articleType}
     and article_content = #{articleContent}
     and file_name like concat('%', #{fileName}, '%')
     and article_atta_url = #{articleAttaUrl}
     and prize >= #{prize}
     and invalid_date = #{invalidDate}
     and create_time >= #{createBeginTime}
     and create_time < #{createEndTime}
     and status = #{status}

order by create_time desc

select ss.user_id, sum(articleCount) as allCount, sum(if(article_type='1',articleCount,0)) as videoCount, sum(if(article_type='2',articleCount,0)) as voiceCount, sum(if(article_type='3',articleCount,0)) as articleCount, sum(if(ss.status='0',articleCount,0)) as activeCount, sum(if(ss.status='1',articleCount,0)) as inActiveCount, sum(ss.prize) as totalPrize, sum(if(ss.status='2',articleCount,0)) as delCount, u.user_name as userName,u.nick_name as nickName,u.telephone,u.school_name as schoolName from ( SELECT user_id, article_type,sum(prize) as prize, status, count(1) AS articleCount FROM t_biz_article
and article_title like concat('%', #{articleTitle}, '%') and user_id = #{userId} and article_type = #{articleType} and file_name like concat('%', #{fileName}, '%') and article_content = #{articleContent} and article_atta_url = #{articleAttaUrl} and status = #{status}
GROUP BY user_id, article_type,status) ss left join t_biz_user u on ss.user_id=u.user_id GROUP BY ss.user_id ORDER BY allCount

mapper.update:
update t_biz_article
user_id = #{userId}, article_title = #{articleTitle}, article_type = #{articleType}, article_content = #{articleContent}, file_name = #{fileName}, article_atta_url = #{articleAttaUrl}, invalid_date = #{invalidDate}, prize = #{prize}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where article_id = #{articleId}
